package fr.paris.lutece.plugins.mylutece.modules.saml.authentication.engine;

import junit.framework.TestCase;

import org.opensaml.DefaultBootstrap;
import org.opensaml.saml2.core.Response;

import fr.paris.lutece.plugins.mylutece.modules.saml.authentication.config.ConfigProperties;
import fr.paris.lutece.plugins.mylutece.modules.saml.authentication.config.Constants;
import fr.paris.lutece.plugins.mylutece.modules.saml.authentication.exceptions.SAMLTokenExtractorException;
import fr.paris.lutece.test.MokeHttpServletRequest;

public class SAMLTokenExtractorTest extends TestCase {

	@Override
	protected void setUp() throws Exception {
		// Initialize the OpenSAML library
		ConfigProperties.getInstance().setTest(true);
		DefaultBootstrap.bootstrap();
	}

	public void testExtractSAMLResponse() {
		MokeHttpServletRequest request = new MokeHttpServletRequest();
		String Base64SAMLResponse = "";
		request.addMokeParameters(Constants.SAML_RESPONSE_REQUEST_PARAM,
				Base64SAMLResponse);

		Response response = null;
		try {
			response = SAMLTokenExtractor.extractSAMLResponse(request);
		} catch (SAMLTokenExtractorException e) {
			// TODO Auto-generated catch block
			fail(e.getLocalizedMessage());
		}

		String IssuerNameID = "urn:fi:dictao:shib:1.0";
		assertEquals("Test SAMLResponse IssuerNameID : " + response.getIssuer().getValue() + ", expected a value of "
		+ IssuerNameID, IssuerNameID, response.getIssuer().getValue());
		
		String Destination = "https://vote.paris.fr:3443/vote/jsp/site/plugins/mylutece/DoMyLuteceLogin.jsp";
		assertEquals("Test SAMLResponse Destination : " + response.getDestination() + ", expected a value of "
				+ Destination, Destination, response.getDestination());
		
	}
}
